INSERT INTO `avn_posts` VALUES("76", "2", "2020-03-22 23:50:06", "2020-03-22 20:20:06", "پس از خرید اپلیکیشن اندروید ، بسته اپلیکیشن اندروید زنبیل رو از سایت راست چین دانلود نمایید. فایل زیپ دانلود شده را اکسترکت نمایید و فایل Help.pdf رو مطالعه کرده و اطلاعات درخواستی را در بخش تیکت اپلیکیشن اندروید زنبیل در سایت راست چین ارسال کنید تا مراحل خروجی اپلیکیشن انجام شود . فایل اپلیکیشن از طریق همان تیکت برای شما ارسال خواهد شد .", "اپلیکیشن اندروید زنبیل خریداری شد فایل اپلیکیشن رو ارسال نمایید", "", "publish", "closed", "closed", "", "%d8%a7%d9%be%d9%84%db%8c%da%a9%db%8c%d8%b4%d9%86-%d8%a7%d9%86%d8%af%d8%b1%d9%88%db%8c%d8%af-%d8%b2%d9%86%d8%a8%db%8c%d9%84-%d8%ae%d8%b1%db%8c%d8%af%d8%a7%d8%b1%db%8c-%d8%b4%d8%af-%d9%81%d8%a7%db%8c", "", "", "2020-03-22 23:50:06", "2020-03-22 20:20:06", "", "0", "http://avin-tarh.ir/?post_type=avinfaq&#038;p=76", "0", "avinfaq", "", "0");
